import React from 'react';
import ReactDOM from 'react-dom';
import './LoginBox.css';
import $ from 'jquery';


$(".email-signup").hide();
$("#signup-box-link").onclick(function(){
  $(".email-login").fadeOut(100);
  $(".email-signup").delay(100).fadeIn(100);
  $("#login-box-link").removeClass("active");
  $("#signup-box-link").addClass("active");
});
$("#login-box-link").onclick(function(){
  $(".email-login").delay(100).fadeIn(100);;
  $(".email-signup").fadeOut(100);
  $("#login-box-link").addClass("active");
  $("#signup-box-link").removeClass("active");
});

export default LoginBox =>
<div class="login-box">
<div class="lb-header">
  <a onclick={LoginBox.loginOnClick} class="active" id="login-box-link">Login</a>
  <a onclick={LoginBox.signUpOnClick} id="signup-box-link">Sign Up</a>
</div>
<div class="social-login">
  <a onclick={LoginBox.facebookOnClick}>
    <i class="fa fa-facebook fa-lg"></i>
    Login com Facebook
  </a>
  <a onclick={LoginBox.googleOnClick}>
    <i class="fa fa-google-plus fa-lg"></i>
    Login com o Google
  </a>
</div>
<form class="email-login">
  <div class="u-form-group">
    <input type="email" placeholder="Email"/>
  </div>
  <div class="u-form-group">
    <input type="password" placeholder="Password"/>
  </div>
  <div class="u-form-group">
    <button>Log in</button>
  </div>
  <div class="u-form-group">
    <a onclick={LoginBox.forgotPasswordOnCLick} class="forgot-password">Esquece a senha?</a>
  </div>
</form>
<form class="email-signup">
  <div class="u-form-group">
    <input type="email" placeholder="Email"/>
  </div>
  <div class="u-form-group">
    <input type="password" placeholder="Password"/>
  </div>
  <div class="u-form-group">
    <input type="password" placeholder="Confirm Password"/>
  </div>
  <div class="u-form-group">
    <button>Cadastrar</button>
  </div>
</form>
</div>
